package StringPart1;

public class conversationMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String sentc = "India  was was my country";
		System.out.println(sentc.toUpperCase());
		
		
		String sent = "INDIA  WAS WAS MY COUNTRY";
		System.out.println(sent.toLowerCase());
		
		int a = 10;
		String x = String.valueOf(a);
		System.out.println(x);
		
		char[] ch = sentc.toCharArray();
		System.out.println(ch);

	}

}
