package StringPart1;

public class positionMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String sentc = "India was  my country";
        System.out.println(sentc.indexOf("w"));
        System.out.println(sentc.lastIndexOf("w"));
        System.out.println(sentc.charAt(6));
        System.out.println(sentc.contains(" "));
        System.out.println(sentc.startsWith("India"));
        System.out.println(sentc.endsWith("country"));

	}

}
